#FUNCTION DEFINITION FOR FUNCTION 1
def add_book(my_dictionary, tup):
    """
    The function adds the book to the dictionary and verifies that the book has been added. The function
    returns the updated dictionary and prints a message stating, “The book has been added
     correctly” or “There was an error adding the book”.
     
    >>>add_book(my_dictionary,("Ulysses","James Joyce","4.5","Sylvia Beach","306","Psychologyyy","English"))
    There was an error adding the book
    {my_dictionary}
    
    
    
    >>>add_book(my_dictionary,("Ulysses","James Joyce","4.5","Sylvia Beach","306","Psychology","English"))
    The book has been added correctly
    {updated my_dictionary}
    
    """

    keys= list(my_dictionary.keys())
    values= list(my_dictionary.values())
    if tup[5] in keys:
        k= list(my_dictionary[tup[5]][0].keys())
        new={k[0]: tup[0],k[1]: tup[1],k[2]: tup[2],k[3]: tup[3],k[4]: tup[4],k[5]: tup[6] }
        my_dictionary[tup[5]].append(new)
        print("The book has been added correctly")
              
    else:
        print("There was an error adding the book")
    
    return my_dictionary



#FUNCTION DEFINITION FOR FUNCTION 2
def remove_book(my_dictionary:dict,title:str,category:str)->dict:
    """
    The function returns the updated dictionary and prints a message stating, “The book has been removed correctly” or “There was an error
    removing the book. Book not found
    
    >>>remove_book(my_dictionary,'How To Win Friends and Influence People',"Psychology")
    The book has been removed correctly
    {updated dictionary}
    
    >>>remove_book(my_dictionary,'Spider-Man: Anti-Venom',"Superheroes")
    The book has been removed correctly
    {updated dictionary}
    

    >>>remove_book(my_dictionary,'PlayStation',"Investing")
    There was an error removing the book. Book not found.
    {updated dictionary}
    """
    

         
    
    if category in my_dictionary.keys():
        for i in range(len(my_dictionary[category])):
            if my_dictionary[category][i]['title'] == title:
                del my_dictionary[category][i]
                print("The book has been removed correctly")
                break
        else:
            print("There was an error removing the book. Book not found.")

    return my_dictionary



#FUNCTION DEFINITION FOR FUNCTION 3
def get_books_by_category(dic:dict,category: str)->int:
    '''Returns a number of books that are within a certain category  
    prints out a statement stating the number of books and what is their title and author
    >>>get_books_by_category(book_category_dictionary('google_books_dataset.csv'),'Fiction')
    29
    >>>get_books_by_category(book_category_dictionary('google_books_dataset.csv'),'Comics')
    6
    >>>get_books_by_category(book_category_dictionary('google_books_dataset.csv'),'Economics')
    17
    '''
    books=[]
    author=[]
    for cate in dic[category]:
        if (cate['title'] and cate['author']) not in (books and author):
            books.append(cate['title'])
            author.append(cate['author'])
    
    print (category + ' has ' + str(len(books)) + ' books. This is the list of books: \n')  
    
    for i in range(len(books and author)):
        print('Book ' + str(i+1) +': ' + books[i] +' by ' + author[i] + '\n')  
       
    
    return len(books)




#FUNCTION DEFINITION FOR FUNCTION 4
def get_books_by_rate(dic:dict,x:int)->int:
    '''Returns a number of books that are within a certain range of a given number 
    prints out a statement stating the number of books and what is their title and author
    >>>get_books_by_rate(book_category_dictionary('google_books_dataset.csv'),3)
    8
    >>>get_books_by_rate(book_category_dictionary('google_books_dataset.csv'),4)
    51
    >>>get_books_by_rate(book_category_dictionary('google_books_dataset.csv'),6)
    0
    
    '''
    book_lst=[]
    author_lst=[]
    for category in dic.keys():
        for rate in dic[category]:
            if rate['rating']!='N/A':
                if int(x)<=rate['rating']<int(x+1):
                    if (rate['title'] and rate['author']) not in (book_lst and author_lst):
                        book_lst.append(rate['title'])
                        author_lst.append(rate['author'])
                        
    if x==5:
        print('There are ' + str(len(book_lst)) + ' books with a rating of ' + str(x)  + '. This is the list of books: \n')
        for i in range(len(book_lst and  author_lst)):
            print ('Book ' + str(i+1) +': ' + book_lst[i] +' by ' + author_lst[i] + '\n')
    elif 3<=x<5:
        print('There are ' + str(len(book_lst)) + ' books with a rating between ' + str(x) + ' and ' + str(x+1) + '. This is the list of books: \n')
        for i in range(len(book_lst and  author_lst)):
            print ('Book ' + str(i+1) +': ' + book_lst[i] +' by ' + author_lst[i] + '\n')        
        
    else:
        print('There are no books with that rating')            
    
    return len(book_lst)





#FUNCTION DEFINITION FOR FUNCTION 5
def get_books_by_title(dictionary:dict,title:str)->bool:

    """
    returns a True Boolean if the title exists in the dictionary and False if the title does not exist 

    >>>get_books_by_title(book_category_dictionary('google_books_dataset.csv'),'The Guardians: The explosive new thriller from international bestseller John Grisham')
    The book has been found
    True
    >>> get_books_by_title(book_category_dictionary('google_books_dataset.csv'),'Twas The Nightshift Before Christmas: Festive hospital diaries from the author of million-copy hit This is Going to Hurt')
    The book has been found
    True
    >>> get_books_by_title(book_category_dictionary('google_books_dataset.csv'),'Taller Tales and Wee Stories: The Best of Billy Connolly')
    The book has NOT been found
    False


    """
    flag = bool()
    #data=[]
    for val in dictionary.values():
        for i in val:
            data = list(i.values())
            if title in data:
                flag = True
    if flag == True:
        print("The book", title, "has been found")

    if flag != True:
        print("The book", title, "has NOT been found")
    return flag




#FUNCTION DEFINITION FOR FUNCTION 6
def get_books_by_author(Dictionary: dict, author_name: str)->int:
    """
    returns a list of title of books written by the given writer and the ratings each book has.
    
    
    >>> get_books_by_author(book_category_dictionary('google_books_dataset.csv'), 'Barbara Allan')
    The author "Barbara Allan" has wrote the following books:
    Book 1: "Antiques Con", rate: "4.8"
    Book 2: "Antiques Knock-Off", rate: "4.3"
    Book 3: "Antiques Roadkill: A Trash 'n' Treasures Mystery", rate: "3.3"
    Book 4: "Antiques Chop", rate: "4.5"
    (['Antiques Con', 'Antiques Knock-Off', "Antiques Roadkill: A Trash 'n' Treasures Mystery", 'Antiques Chop'], ['4.8', '4.3', '3.3', '4.5'])
    >>> get_books_by_author(book_category_dictionary('google_books_dataset.csv'), 'Alvin Hall')
    The author "Alvin Hall" has wrote the following books:
    Book 1: "The Secrets of Saving and Investing with Alvin Hall: Simple Strategies to Make Your Money Go Further", rate: "3.7"
    (['The Secrets of Saving and Investing with Alvin Hall: Simple Strategies to Make Your Money Go Further'], ['3.7'])
    >>> get_books_by_author(book_category_dictionary('google_books_dataset.csv'), 'David Baldacci')
    The author "David Baldacci" has wrote the following books:
    Book 1: "Total Control", rate: "4"
    (['Total Control'], ['4'])
    """
    new_list = []
    books = []
    rating = []
    for key in list(Dictionary.keys()):
        for i in list(Dictionary.get(key)):
            if i not in new_list:
                new_list.append(i)
    for i in new_list:
        if i.get('author') == author_name and i.get('title') not in books:
            books.append(i.get('title'))
            rating.append(i.get('rating'))

    if books != []:
        print('The author \"' + author_name + '\" has published the following books:')  
        for i in range(len(books)):
            print('Book ' + str(i+1) +': '+ '"' + books[i] + '"' + ', rate: ' + '"' + str(rating[i])+'"'+'\n' )    
    else:
        print('There are no books with this author in the data set.')
    return len(books)



