def get_books_by_publisher(Dictionary: dict, Publisher_name: str)->int:
    """
    returns a list with the book published by the particular publisher
    
    >>>get_books_by_publisher(book_category_dictionary('google_books_dataset.csv'), 'AMACOM')
       5
    >>>get_books_by_publisher(book_category_dictionary('google_books_dataset.csv'), 'Harper Collins')
       4
    >>>get_books_by_publisher(book_category_dictionary('google_books_dataset.csv'), 'Simon and Schuster')  
       4
    """
    i = 0
    n = 1
    new_list = []
    book_list = []
    book_author = []
    for key in Dictionary.keys():
        for i in Dictionary.get(key):
            if i not in new_list:
                new_list.append(i)
                
    for i in new_list:
        if i.get('publisher') == Publisher_name and i.get('title') not in book_list:
            book_list.append(i.get('title'))
            book_author.append(i.get('author'))
    if book_list != []:
        print('The publisher \"' + Publisher_name + '\" has published the following books:')  
        for i in book_list:
            print('Book', str(n) + ' - ' + str(i), end= '')
            print('by', book_author[n-1])
            n += 1
    else:
        print('There are no books with this publisher in the data set.')
    return len(book_list)




