def  get_all_categories_for_book_title(Dictionary: dict, TITLE: str)->int:
    """
    returns a list of genres of the book title
    
    >>>get_all_categories_for_book_title(book_category_dictionary('google_books_dataset.csv'),  'The Guardians: The explosive new thriller from international bestseller John Grisham')
       3
    >>>get_all_categories_for_book_title(book_category_dictionary('google_books_dataset.csv'), 'Young Justice Vol. 1') 
       2
    >>>get_all_categories_for_book_title(book_category_dictionary('google_books_dataset.csv'), 'Antiques Con') 
       3
    """
    book_list = []
    n = 0
    for key in Dictionary.keys():
        for i in Dictionary.get(key):
            if i.get('title') == TITLE:
                book_list.append(key)
                n += 1
    print('The book title \"' + TITLE + '\" has the following categories:')  
    q = 1
    for i in range(n):
        print(str(q) + ' - ' + str(book_list[i]))
        q += 1    
    return len(book_list) 




#TEST FUNCTION FOR AUTOMATED TESTING
def check_equal(description: str, expected, actual)->None:
    """
    Checks if expected result matches actual result. Checks first if type matches
    expected, and then if the value matches expected. If it matches, the test
    passes. If not, the test fails.
    """

    if type(actual) != type(expected):
       
        print("{0} FAILED: Expected ({1}) type {2}, Actual ({3}) type {4}".
              format(description, expected, str(type(expected)).strip('<class>'),
                      actual, str(type(actual)).strip('<class> ')))
    elif actual != expected:
        print("{0} FAILED: Expected: {1}, Actual: {2}".
              format(description, expected, actual))
    else:
        print("{0} PASSED".format(description)) 
    print("------")

 #For total automated testing
    if expected == actual:
            return True
    else:
            return False 
        


